import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styles from './ContentManager.module.css';

const ContentManager = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tabParam = queryParams.get('tab');
  
  const [mode, setMode] = useState(tabParam === 'expression' ? 'expression' : 'word'); // 'word' or 'expression'
  
  // Word state
  const [words, setWords] = useState([]);
  const [currentWord, setCurrentWord] = useState({
    id: Date.now(),
    polish: '',
    english: '',
    example: '',
    notes: ''
  });
  
  // Expression state
  const [expressions, setExpressions] = useState([]);
  const [currentExpression, setCurrentExpression] = useState({
    id: Date.now(),
    polish: '',
    english: '',
    notes: ''
  });
  
  const [errors, setErrors] = useState({});

  // Load saved data from localStorage
  useEffect(() => {
    const savedWords = localStorage.getItem('polish-words');
    if (savedWords) {
      setWords(JSON.parse(savedWords));
    }
    
    const savedExpressions = localStorage.getItem('polish-expressions');
    if (savedExpressions) {
      setExpressions(JSON.parse(savedExpressions));
    }
  }, []);

  // Save data to localStorage when they change
  useEffect(() => {
    localStorage.setItem('polish-words', JSON.stringify(words));
  }, [words]);
  
  useEffect(() => {
    localStorage.setItem('polish-expressions', JSON.stringify(expressions));
  }, [expressions]);

  const validateWord = () => {
    const errors = {};
    if (!currentWord.polish.trim()) errors.polish = "Polish word is required";
    if (!currentWord.english.trim()) errors.english = "English translation is required";
    return errors;
  };

  const validateExpression = () => {
    const errors = {};
    if (!currentExpression.polish.trim()) errors.polish = "Polish expression is required";
    if (!currentExpression.english.trim()) errors.english = "English translation is required";
    return errors;
  };

  const handleWordChange = (e) => {
    const { name, value } = e.target;
    setCurrentWord(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleExpressionChange = (e) => {
    const { name, value } = e.target;
    setCurrentExpression(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const saveWord = () => {
    const validationErrors = validateWord();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Add new word
    setWords(prev => [...prev, currentWord]);
    
    // Reset form
    setCurrentWord({
      id: Date.now(),
      polish: '',
      english: '',
      example: '',
      notes: ''
    });
    
    setErrors({});
    alert('Word saved successfully!');
  };

  const saveExpression = () => {
    const validationErrors = validateExpression();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Add new expression
    setExpressions(prev => [...prev, currentExpression]);
    
    // Reset form
    setCurrentExpression({
      id: Date.now(),
      polish: '',
      english: '',
      notes: ''
    });
    
    setErrors({});
    alert('Expression saved successfully!');
  };

  return (
    <div className={styles.contentManager}>
      <div className={styles.header}>
        <h1>Add Polish Learning Content</h1>
        <p>Add words and expressions to your Polish learning collection</p>
      </div>
      
      <div className={styles.tabs}>
        <button 
          className={`${styles.tab} ${mode === 'word' ? styles.active : ''}`}
          onClick={() => setMode('word')}
        >
          Add Word
        </button>
        <button 
          className={`${styles.tab} ${mode === 'expression' ? styles.active : ''}`}
          onClick={() => setMode('expression')}
        >
          Add Expression
        </button>
      </div>
      
      {mode === 'word' ? (
        <div className={styles.wordForm}>
          <h2>Add New Polish Word</h2>
          
          <div className={styles.formGroup}>
            <label htmlFor="polish">Polish Word:</label>
            <input
              type="text"
              id="polish"
              name="polish"
              value={currentWord.polish}
              onChange={handleWordChange}
              placeholder="e.g., książka"
            />
            {errors.polish && <div className={styles.error}>{errors.polish}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="english">English Translation:</label>
            <input
              type="text"
              id="english"
              name="english"
              value={currentWord.english}
              onChange={handleWordChange}
              placeholder="e.g., book"
            />
            {errors.english && <div className={styles.error}>{errors.english}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="example">Example Sentence (optional):</label>
            <textarea
              id="example"
              name="example"
              value={currentWord.example}
              onChange={handleWordChange}
              placeholder="e.g., Ta książka jest interesująca."
              rows="3"
            ></textarea>
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="notes">Notes (optional):</label>
            <textarea
              id="notes"
              name="notes"
              value={currentWord.notes}
              onChange={handleWordChange}
              placeholder="Any additional notes about this word..."
              rows="2"
            ></textarea>
          </div>
          
          <div className={styles.formActions}>
            <button 
              type="button"
              className={styles.saveButton}
              onClick={saveWord}
            >
              Save Word
            </button>
          </div>
          
          <div className={styles.recentItems}>
            <h3>Recently Added Words</h3>
            {words.length === 0 ? (
              <p className={styles.noItems}>No words added yet</p>
            ) : (
              <div className={styles.itemsList}>
                {words.slice(-5).reverse().map((word) => (
                  <div key={word.id} className={styles.item}>
                    <span className={styles.itemPolish}>{word.polish}</span>
                    <span className={styles.itemDivider}>-</span>
                    <span className={styles.itemEnglish}>{word.english}</span>
                  </div>
                ))}
                <Link to="/words" className={styles.viewAllLink}>View all words →</Link>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className={styles.expressionForm}>
          <h2>Add New Polish Expression</h2>
          
          <div className={styles.formGroup}>
            <label htmlFor="polish-expr">Polish Expression:</label>
            <textarea
              id="polish-expr"
              name="polish"
              value={currentExpression.polish}
              onChange={handleExpressionChange}
              placeholder="e.g., Nie ma za co"
              rows="2"
            ></textarea>
            {errors.polish && <div className={styles.error}>{errors.polish}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="english-expr">English Translation:</label>
            <textarea
              id="english-expr"
              name="english"
              value={currentExpression.english}
              onChange={handleExpressionChange}
              placeholder="e.g., You're welcome / Don't mention it"
              rows="2"
            ></textarea>
            {errors.english && <div className={styles.error}>{errors.english}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="notes-expr">Notes (optional):</label>
            <textarea
              id="notes-expr"
              name="notes"
              value={currentExpression.notes}
              onChange={handleExpressionChange}
              placeholder="Any additional notes about this expression..."
              rows="2"
            ></textarea>
          </div>
          
          <div className={styles.formActions}>
            <button 
              type="button"
              className={styles.saveButton}
              onClick={saveExpression}
            >
              Save Expression
            </button>
          </div>
          
          <div className={styles.recentItems}>
            <h3>Recently Added Expressions</h3>
            {expressions.length === 0 ? (
              <p className={styles.noItems}>No expressions added yet</p>
            ) : (
              <div className={styles.itemsList}>
                {expressions.slice(-5).reverse().map((expression) => (
                  <div key={expression.id} className={styles.item}>
                    <span className={styles.itemPolish}>{expression.polish}</span>
                    <span className={styles.itemDivider}>-</span>
                    <span className={styles.itemEnglish}>{expression.english}</span>
                  </div>
                ))}
                <Link to="/expressions" className={styles.viewAllLink}>View all expressions →</Link>
              </div>
            )}
          </div>
        </div>
      )}
      
      <div className={styles.navigation}>
        <Link to="/" className="btn btn-secondary">Back to Dashboard</Link>
      </div>
    </div>
  );
};

export default ContentManager;
