import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './Dashboard.module.css';

const Dashboard = () => {
  const [words, setWords] = useState([]);
  const [expressions, setExpressions] = useState([]);
  const [streak, setStreak] = useState(0);
  const [lastUsedDate, setLastUsedDate] = useState(null);
  const [wrongAnswers, setWrongAnswers] = useState({ words: [], expressions: [] });
  const [notes, setNotes] = useState('');
  const fileInputRef = useRef(null);

  // Load data from localStorage
  useEffect(() => {
    // Load words and expressions
    const savedWords = localStorage.getItem('polish-words');
    if (savedWords) {
      setWords(JSON.parse(savedWords));
    }
    
    const savedExpressions = localStorage.getItem('polish-expressions');
    if (savedExpressions) {
      setExpressions(JSON.parse(savedExpressions));
    }
    
    // Load streak data
    const streakData = localStorage.getItem('polish-streak');
    if (streakData) {
      const { streak, lastUsed } = JSON.parse(streakData);
      setStreak(streak);
      setLastUsedDate(new Date(lastUsed));
    }
    
    // Load wrong answers data
    const wrongAnswersData = localStorage.getItem('polish-wrong-answers');
    if (wrongAnswersData) {
      setWrongAnswers(JSON.parse(wrongAnswersData));
    }
    
    // Load notes
    const savedNotes = localStorage.getItem('polish-notes');
    if (savedNotes) {
      setNotes(savedNotes);
    }
    
    // Check and update streak
    updateStreak();
  }, []);
  
  // Update streak based on current date
  const updateStreak = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Normalize to start of day
    
    const storedStreak = localStorage.getItem('polish-streak');
    let currentStreak = 0;
    let lastUsed = null;
    
    if (storedStreak) {
      const streakData = JSON.parse(storedStreak);
      currentStreak = streakData.streak;
      lastUsed = new Date(streakData.lastUsed);
      lastUsed.setHours(0, 0, 0, 0); // Normalize to start of day
      
      const yesterday = new Date(today);
      yesterday.setDate(yesterday.getDate() - 1);
      
      if (lastUsed.getTime() === today.getTime()) {
        // Already logged in today, do nothing
      } else if (lastUsed.getTime() === yesterday.getTime()) {
        // Logged in yesterday, increment streak
        currentStreak += 1;
      } else {
        // Streak broken
        currentStreak = 1;
      }
    } else {
      // First time user
      currentStreak = 1;
    }
    
    // Save updated streak
    const updatedStreakData = {
      streak: currentStreak,
      lastUsed: today.toISOString()
    };
    
    localStorage.setItem('polish-streak', JSON.stringify(updatedStreakData));
    setStreak(currentStreak);
    setLastUsedDate(today);
  };

  // Export all data as a single JSON file
  const exportAllData = () => {
    const allData = {
      words,
      expressions,
      streak: { streak, lastUsed: lastUsedDate?.toISOString() },
      wrongAnswers,
      notes
    };
    
    const jsonString = JSON.stringify(allData, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    
    const fileName = `polish-learning-data-${new Date().toISOString().slice(0, 10)}.json`;
    
    // Create download link and click it
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    link.click();
    
    // Clean up
    URL.revokeObjectURL(url);
  };
  
  // Import data from a JSON file
  const importAllData = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    
    reader.onload = (event) => {
      try {
        const parsedData = JSON.parse(event.target.result);
        
        // Validate data structure
        if (!parsedData.words || !Array.isArray(parsedData.words)) {
          throw new Error('Invalid format: Missing or invalid words data');
        }
        
        if (!parsedData.expressions || !Array.isArray(parsedData.expressions)) {
          throw new Error('Invalid format: Missing or invalid expressions data');
        }
        
        // Import the data
        localStorage.setItem('polish-words', JSON.stringify(parsedData.words || []));
        localStorage.setItem('polish-expressions', JSON.stringify(parsedData.expressions || []));
        
        if (parsedData.streak) {
          localStorage.setItem('polish-streak', JSON.stringify(parsedData.streak));
        }
        
        if (parsedData.wrongAnswers) {
          localStorage.setItem('polish-wrong-answers', JSON.stringify(parsedData.wrongAnswers));
        }
        
        if (parsedData.notes) {
          localStorage.setItem('polish-notes', parsedData.notes);
        }
        
        // Update state with the new data
        setWords(parsedData.words || []);
        setExpressions(parsedData.expressions || []);
        
        if (parsedData.streak) {
          setStreak(parsedData.streak.streak || 0);
          setLastUsedDate(parsedData.streak.lastUsed ? new Date(parsedData.streak.lastUsed) : null);
        }
        
        setWrongAnswers(parsedData.wrongAnswers || { words: [], expressions: [] });
        
        setNotes(parsedData.notes || '');
        
        alert('Îç∞Ïù¥ÌÑ∞Î•º ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Í∞ÄÏ†∏ÏôîÏäµÎãàÎã§!');
        
      } catch (error) {
        alert(`Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïò§Î•ò: ${error.message}`);
      }
      
      // Reset file input
      fileInputRef.current.value = '';
    };
    
    reader.readAsText(file);
  };

  return (
    <div className={styles.dashboard}>
      <div className={styles.header}>
        <h1>Polish Learning Dashboard</h1>
        <p>Track your progress and take quizzes to improve your Polish language skills</p>
      </div>

      <div className={styles.statsSection}>
        <h2>Your Progress</h2>
        <div className={styles.statsGrid}>
          <div className={styles.statCard}>
            <div className={styles.statIcon}>üìö</div>
            <div className={styles.statTitle}>Words</div>
            <div className={styles.statNumber}>{words.length}</div>
            <Link to="/words" className={styles.statLink}>View all words ‚Üí</Link>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>üí¨</div>
            <div className={styles.statTitle}>Expressions</div>
            <div className={styles.statNumber}>{expressions.length}</div>
            <Link to="/expressions" className={styles.statLink}>View all expressions ‚Üí</Link>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>üî•</div>
            <div className={styles.statTitle}>Daily Streak</div>
            <div className={styles.statNumber}>{streak}</div>
            <div className={styles.statSubtext}>
              days in a row
            </div>
          </div>
        </div>
      </div>

      <div className={styles.backupSection}>
        <h2>Îç∞Ïù¥ÌÑ∞ Î∞±ÏóÖ Î∞è Î≥µÏõê</h2>
        <div className={styles.backupCard}>
          <p>Î™®Îì† ÌïôÏäµ Îç∞Ïù¥ÌÑ∞(Îã®Ïñ¥, ÌëúÌòÑ, Ïä§Ìä∏Î¶≠, ÎÖ∏Ìä∏ Îì±)ÏùÑ Ìïú Î≤àÏóê Î∞±ÏóÖÌïòÍ±∞ÎÇò Î≥µÏõêÌï† Ïàò ÏûàÏäµÎãàÎã§.</p>
          
          <div className={styles.backupButtons}>
            <button 
              className={styles.exportButton}
              onClick={exportAllData}
              disabled={words.length === 0 && expressions.length === 0}
            >
              Î™®Îì† Îç∞Ïù¥ÌÑ∞ ÎÇ¥Î≥¥ÎÇ¥Í∏∞
            </button>
            
            <div className={styles.importWrapper}>
              <input
                type="file"
                ref={fileInputRef}
                onChange={importAllData}
                accept=".json"
                id="import-all-data"
                className={styles.fileInput}
              />
              <label htmlFor="import-all-data" className={styles.importButton}>
                Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
              </label>
            </div>
          </div>
        </div>
      </div>

      <div className={styles.quizSection}>
        <h2>How Far Can You Go?</h2>
        <div className={styles.quizCards}>
          <div className={styles.quizCard}>
            <h3>Words Challenge</h3>
            <p>Test your knowledge on all {words.length} Polish words you've learned</p>
            <Link to="/quiz?type=word&mode=all" className="btn btn-primary">
              Start Words Quiz
            </Link>
          </div>
          
          <div className={styles.quizCard}>
            <h3>Expressions Challenge</h3>
            <p>Test your knowledge on all {expressions.length} Polish expressions you've learned</p>
            <Link to="/quiz?type=expression&mode=all" className="btn btn-primary">
              Start Expressions Quiz
            </Link>
          </div>
        </div>
      </div>

      <div className={styles.wrongAnswersSection}>
        <h2>Words & Expressions to Review</h2>
        {(wrongAnswers.words.length === 0 && wrongAnswers.expressions.length === 0) ? (
          <div className="card">
            <p>No difficult words or expressions yet. Take some quizzes to see what you need to practice!</p>
          </div>
        ) : (
          <div className={styles.reviewCards}>
            {wrongAnswers.words.length > 0 && (
              <div className={styles.reviewCard}>
                <h3>Difficult Words</h3>
                <ul className={styles.reviewList}>
                  {wrongAnswers.words.slice(0, 3).map((item, index) => (
                    <li key={index} className={styles.reviewItem}>
                      <span className={styles.reviewPolish}>{item.polish}</span>
                      <span className={styles.reviewEnglish}>{item.english}</span>
                      <span className={styles.reviewCount}>
                        Missed {item.count} {item.count === 1 ? 'time' : 'times'}
                      </span>
                    </li>
                  ))}
                </ul>
                <Link to="/quiz?type=word&mode=review" className="btn btn-secondary">
                  Practice Difficult Words
                </Link>
              </div>
            )}
            
            {wrongAnswers.expressions.length > 0 && (
              <div className={styles.reviewCard}>
                <h3>Difficult Expressions</h3>
                <ul className={styles.reviewList}>
                  {wrongAnswers.expressions.slice(0, 3).map((item, index) => (
                    <li key={index} className={styles.reviewItem}>
                      <span className={styles.reviewPolish}>{item.polish}</span>
                      <span className={styles.reviewEnglish}>{item.english}</span>
                      <span className={styles.reviewCount}>
                        Missed {item.count} {item.count === 1 ? 'time' : 'times'}
                      </span>
                    </li>
                  ))}
                </ul>
                <Link to="/quiz?type=expression&mode=review" className="btn btn-secondary">
                  Practice Difficult Expressions
                </Link>
              </div>
            )}
          </div>
        )}
      </div>
      
      <div className={styles.addContentSection}>
        <h2>Expand Your Polish Vocabulary</h2>
        <div className={styles.addButtons}>
          <Link to="/content-manager" className="btn btn-primary">
            Add New Words
          </Link>
          <Link to="/content-manager?tab=expression" className="btn btn-primary">
            Add New Expressions
          </Link>
          <Link to="/notes" className="btn btn-secondary">
            View Your Notes
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
