import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styles from './ContentManager.module.css';

const ContentManager = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const tabParam = queryParams.get('tab');
  
  const [mode, setMode] = useState(tabParam === 'expression' ? 'expression' : 'word'); // 'word' or 'expression'
  
  // Word state
  const [words, setWords] = useState([]);
  const [currentWord, setCurrentWord] = useState({
    id: Date.now(),
    polish: '',
    english: '',
    example: '',
    notes: ''
  });
  
  // Expression state
  const [expressions, setExpressions] = useState([]);
  const [currentExpression, setCurrentExpression] = useState({
    id: Date.now(),
    polish: '',
    english: '',
    notes: ''
  });
  
  const [errors, setErrors] = useState({});
  
  // File input refs for importing data
  const wordFileInputRef = useRef(null);
  const expressionFileInputRef = useRef(null);

  // Load saved data from localStorage
  useEffect(() => {
    const savedWords = localStorage.getItem('polish-words');
    if (savedWords) {
      setWords(JSON.parse(savedWords));
    }
    
    const savedExpressions = localStorage.getItem('polish-expressions');
    if (savedExpressions) {
      setExpressions(JSON.parse(savedExpressions));
    }
  }, []);

  // Save data to localStorage when they change
  useEffect(() => {
    localStorage.setItem('polish-words', JSON.stringify(words));
  }, [words]);
  
  useEffect(() => {
    localStorage.setItem('polish-expressions', JSON.stringify(expressions));
  }, [expressions]);

  const validateWord = () => {
    const errors = {};
    if (!currentWord.polish.trim()) errors.polish = "Polish word is required";
    if (!currentWord.english.trim()) errors.english = "English translation is required";
    return errors;
  };

  const validateExpression = () => {
    const errors = {};
    if (!currentExpression.polish.trim()) errors.polish = "Polish expression is required";
    if (!currentExpression.english.trim()) errors.english = "English translation is required";
    return errors;
  };

  const handleWordChange = (e) => {
    const { name, value } = e.target;
    setCurrentWord(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleExpressionChange = (e) => {
    const { name, value } = e.target;
    setCurrentExpression(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const saveWord = () => {
    const validationErrors = validateWord();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Add new word
    setWords(prev => [...prev, currentWord]);
    
    // Reset form
    setCurrentWord({
      id: Date.now(),
      polish: '',
      english: '',
      example: '',
      notes: ''
    });
    
    setErrors({});
    alert('Word saved successfully!');
  };

  const saveExpression = () => {
    const validationErrors = validateExpression();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Add new expression
    setExpressions(prev => [...prev, currentExpression]);
    
    // Reset form
    setCurrentExpression({
      id: Date.now(),
      polish: '',
      english: '',
      notes: ''
    });
    
    setErrors({});
    alert('Expression saved successfully!');
  };
  
  // Export data as JSON
  const exportData = (data, fileName) => {
    const jsonString = JSON.stringify(data, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    
    // Create download link and click it
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    link.click();
    
    // Clean up
    URL.revokeObjectURL(url);
  };
  
  const handleExportWords = () => {
    if (words.length === 0) {
      alert('내보낼 단어가 없습니다!');
      return;
    }
    
    const fileName = `polish-words-${new Date().toISOString().slice(0, 10)}.json`;
    exportData(words, fileName);
  };
  
  const handleExportExpressions = () => {
    if (expressions.length === 0) {
      alert('내보낼 표현이 없습니다!');
      return;
    }
    
    const fileName = `polish-expressions-${new Date().toISOString().slice(0, 10)}.json`;
    exportData(expressions, fileName);
  };
  
  // Import data from JSON file
  const handleFileSelect = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    
    reader.onload = (event) => {
      try {
        const parsedData = JSON.parse(event.target.result);
        
        if (!Array.isArray(parsedData)) {
          throw new Error('Invalid format: Data is not an array');
        }
        
        // Validate data structure
        const invalidItems = parsedData.filter(item => {
          if (type === 'words' && (!item.polish || !item.english)) return true;
          if (type === 'expressions' && (!item.polish || !item.english)) return true;
          return false;
        });
        
        if (invalidItems.length > 0) {
          throw new Error(`Invalid format: ${invalidItems.length} items are missing required fields`);
        }
        
        // Add unique IDs if missing
        const dataWithIds = parsedData.map(item => ({
          ...item,
          id: item.id || Date.now() + Math.floor(Math.random() * 1000)
        }));
        
        // Merge with existing data (avoiding duplicates based on Polish text)
        if (type === 'words') {
          const existingPolishWords = new Set(words.map(word => word.polish));
          const newWords = dataWithIds.filter(word => !existingPolishWords.has(word.polish));
          setWords(prev => [...prev, ...newWords]);
          alert(`Imported ${newWords.length} new words.`);
        } else {
          const existingPolishExpressions = new Set(expressions.map(expr => expr.polish));
          const newExpressions = dataWithIds.filter(expr => !existingPolishExpressions.has(expr.polish));
          setExpressions(prev => [...prev, ...newExpressions]);
          alert(`Imported ${newExpressions.length} new expressions.`);
        }
        
      } catch (error) {
        alert(`Error importing file: ${error.message}`);
      }
      
      // Reset the file input
      if (type === 'words') {
        wordFileInputRef.current.value = '';
      } else {
        expressionFileInputRef.current.value = '';
      }
    };
    
    reader.readAsText(file);
  };

  return (
    <div className={styles.contentManager}>
      <div className={styles.header}>
        <h1>Add Polish Learning Content</h1>
        <p>Add words and expressions to your Polish learning collection</p>
      </div>
      
      <div className={styles.tabs}>
        <button 
          className={`${styles.tab} ${mode === 'word' ? styles.active : ''}`}
          onClick={() => setMode('word')}
        >
          Add Word
        </button>
        <button 
          className={`${styles.tab} ${mode === 'expression' ? styles.active : ''}`}
          onClick={() => setMode('expression')}
        >
          Add Expression
        </button>
      </div>
      
      {mode === 'word' ? (
        <div className={styles.wordForm}>
          <h2>Add New Polish Word</h2>
          
          <div className={styles.formGroup}>
            <label htmlFor="polish">Polish Word:</label>
            <input
              type="text"
              id="polish"
              name="polish"
              value={currentWord.polish}
              onChange={handleWordChange}
              placeholder="e.g., książka"
            />
            {errors.polish && <div className={styles.error}>{errors.polish}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="english">English Translation:</label>
            <input
              type="text"
              id="english"
              name="english"
              value={currentWord.english}
              onChange={handleWordChange}
              placeholder="e.g., book"
            />
            {errors.english && <div className={styles.error}>{errors.english}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="example">Example Sentence (optional):</label>
            <textarea
              id="example"
              name="example"
              value={currentWord.example}
              onChange={handleWordChange}
              placeholder="e.g., Ta książka jest interesująca."
              rows="3"
            ></textarea>
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="notes">Notes (optional):</label>
            <textarea
              id="notes"
              name="notes"
              value={currentWord.notes}
              onChange={handleWordChange}
              placeholder="Any additional notes about this word..."
              rows="2"
            ></textarea>
          </div>
          
          <div className={styles.formActions}>
            <button 
              type="button"
              className={styles.saveButton}
              onClick={saveWord}
            >
              Save Word
            </button>
          </div>
          
          <div className={styles.jsonActions}>
            <div className={styles.exportImport}>
              <h3>Manage Your Word Collection</h3>
              <div className={styles.jsonButtons}>
                <div className={styles.jsonAction}>
                  <button 
                    className={styles.exportButton}
                    onClick={handleExportWords}
                    disabled={words.length === 0}
                  >
                    Export Words as JSON
                  </button>
                  <p className={styles.actionHint}>Save your word collection to your computer</p>
                </div>
                
                <div className={styles.jsonAction}>
                  <input
                    type="file"
                    ref={wordFileInputRef}
                    onChange={(e) => handleFileSelect(e, 'words')}
                    accept=".json"
                    id="import-words"
                    className={styles.fileInput}
                  />
                  <label htmlFor="import-words" className={styles.importButton}>
                    Import Words from JSON
                  </label>
                  <p className={styles.actionHint}>Load words from a previously exported JSON file</p>
                </div>
              </div>
            </div>
          </div>
          
          <div className={styles.recentItems}>
            <h3>Recently Added Words</h3>
            {words.length === 0 ? (
              <p className={styles.noItems}>No words added yet</p>
            ) : (
              <div className={styles.itemsList}>
                {words.slice(-5).reverse().map((word) => (
                  <div key={word.id} className={styles.item}>
                    <span className={styles.itemPolish}>{word.polish}</span>
                    <span className={styles.itemDivider}>-</span>
                    <span className={styles.itemEnglish}>{word.english}</span>
                  </div>
                ))}
                <Link to="/words" className={styles.viewAllLink}>View all words →</Link>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className={styles.expressionForm}>
          <h2>Add New Polish Expression</h2>
          
          <div className={styles.formGroup}>
            <label htmlFor="polish-expr">Polish Expression:</label>
            <textarea
              id="polish-expr"
              name="polish"
              value={currentExpression.polish}
              onChange={handleExpressionChange}
              placeholder="e.g., Nie ma za co"
              rows="2"
            ></textarea>
            {errors.polish && <div className={styles.error}>{errors.polish}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="english-expr">English Translation:</label>
            <textarea
              id="english-expr"
              name="english"
              value={currentExpression.english}
              onChange={handleExpressionChange}
              placeholder="e.g., You're welcome / Don't mention it"
              rows="2"
            ></textarea>
            {errors.english && <div className={styles.error}>{errors.english}</div>}
          </div>
          
          <div className={styles.formGroup}>
            <label htmlFor="notes-expr">Notes (optional):</label>
            <textarea
              id="notes-expr"
              name="notes"
              value={currentExpression.notes}
              onChange={handleExpressionChange}
              placeholder="Any additional notes about this expression..."
              rows="2"
            ></textarea>
          </div>
          
          <div className={styles.formActions}>
            <button 
              type="button"
              className={styles.saveButton}
              onClick={saveExpression}
            >
              Save Expression
            </button>
          </div>
          
          <div className={styles.jsonActions}>
            <div className={styles.exportImport}>
              <h3>Manage Your Expression Collection</h3>
              <div className={styles.jsonButtons}>
                <div className={styles.jsonAction}>
                  <button 
                    className={styles.exportButton}
                    onClick={handleExportExpressions}
                    disabled={expressions.length === 0}
                  >
                    Export Expressions as JSON
                  </button>
                  <p className={styles.actionHint}>Save your expression collection to your computer</p>
                </div>
                
                <div className={styles.jsonAction}>
                  <input
                    type="file"
                    ref={expressionFileInputRef}
                    onChange={(e) => handleFileSelect(e, 'expressions')}
                    accept=".json"
                    id="import-expressions"
                    className={styles.fileInput}
                  />
                  <label htmlFor="import-expressions" className={styles.importButton}>
                    Import Expressions from JSON
                  </label>
                  <p className={styles.actionHint}>Load expressions from a previously exported JSON file</p>
                </div>
              </div>
            </div>
          </div>
          
          <div className={styles.recentItems}>
            <h3>Recently Added Expressions</h3>
            {expressions.length === 0 ? (
              <p className={styles.noItems}>No expressions added yet</p>
            ) : (
              <div className={styles.itemsList}>
                {expressions.slice(-5).reverse().map((expression) => (
                  <div key={expression.id} className={styles.item}>
                    <span className={styles.itemPolish}>{expression.polish}</span>
                    <span className={styles.itemDivider}>-</span>
                    <span className={styles.itemEnglish}>{expression.english}</span>
                  </div>
                ))}
                <Link to="/expressions" className={styles.viewAllLink}>View all expressions →</Link>
              </div>
            )}
          </div>
        </div>
      )}
      
      <div className={styles.navigation}>
        <Link to="/" className="btn btn-secondary">Back to Dashboard</Link>
      </div>
    </div>
  );
};

export default ContentManager;
