import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './WordsPage.module.css';

const WordsPage = () => {
  const [words, setWords] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('dateAdded'); // 'dateAdded', 'polish', 'english'
  const [sortOrder, setSortOrder] = useState('desc'); // 'asc', 'desc'
  const fileInputRef = useRef(null);

  useEffect(() => {
    const savedWords = localStorage.getItem('polish-words');
    if (savedWords) {
      setWords(JSON.parse(savedWords));
    }
  }, []);

  // Save to localStorage whenever words change
  useEffect(() => {
    localStorage.setItem('polish-words', JSON.stringify(words));
  }, [words]);

  const handleDeleteWord = (id) => {
    if (window.confirm('Are you sure you want to delete this word?')) {
      const updatedWords = words.filter(word => word.id !== id);
      setWords(updatedWords);
      localStorage.setItem('polish-words', JSON.stringify(updatedWords));
    }
  };

  const handleSortChange = (newSortBy) => {
    if (sortBy === newSortBy) {
      // If clicking the same column, toggle sort order
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      // If clicking a different column, set it as the sort column
      setSortBy(newSortBy);
      setSortOrder('asc');
    }
  };
  
  // Export data as JSON
  const exportWords = () => {
    if (words.length === 0) {
      alert('내보낼 단어가 없습니다!');
      return;
    }
    
    const jsonString = JSON.stringify(words, null, 2);
    const blob = new Blob([jsonString], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    
    const fileName = `polish-words-${new Date().toISOString().slice(0, 10)}.json`;
    
    // Create download link and click it
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    link.click();
    
    // Clean up
    URL.revokeObjectURL(url);
  };
  
  // Import data from JSON file
  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    
    reader.onload = (event) => {
      try {
        const parsedData = JSON.parse(event.target.result);
        
        if (!Array.isArray(parsedData)) {
          throw new Error('Invalid format: Data is not an array');
        }
        
        // Validate data structure
        const invalidItems = parsedData.filter(item => !item.polish || !item.english);
        
        if (invalidItems.length > 0) {
          throw new Error(`Invalid format: ${invalidItems.length} items are missing required fields`);
        }
        
        // Add unique IDs if missing
        const dataWithIds = parsedData.map(item => ({
          ...item,
          id: item.id || Date.now() + Math.floor(Math.random() * 1000)
        }));
        
        // Merge with existing data (avoiding duplicates based on Polish text)
        const existingPolishWords = new Set(words.map(word => word.polish));
        const newWords = dataWithIds.filter(word => !existingPolishWords.has(word.polish));
        const updatedWords = [...words, ...newWords];
        
        setWords(updatedWords);
        localStorage.setItem('polish-words', JSON.stringify(updatedWords));
        alert(`Imported ${newWords.length} new words.`);
        
      } catch (error) {
        alert(`Error importing file: ${error.message}`);
      }
      
      // Reset the file input
      fileInputRef.current.value = '';
    };
    
    reader.readAsText(file);
  };

  // Filter words based on search term
  const filteredWords = words.filter(word => 
    word.polish.toLowerCase().includes(searchTerm.toLowerCase()) ||
    word.english.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sort words based on sort criteria
  const sortedWords = [...filteredWords].sort((a, b) => {
    let comparison = 0;

    if (sortBy === 'dateAdded') {
      comparison = a.id - b.id;
    } else if (sortBy === 'polish') {
      comparison = a.polish.localeCompare(b.polish);
    } else if (sortBy === 'english') {
      comparison = a.english.localeCompare(b.english);
    }

    return sortOrder === 'asc' ? comparison : -comparison;
  });

  return (
    <div className={styles.wordsPage}>
      <div className={styles.header}>
        <h1>Polish Words Collection</h1>
        <p>All the Polish words you've added to your collection</p>
      </div>

      <div className={styles.controls}>
        <div className={styles.search}>
          <input
            type="text"
            placeholder="Search words..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className={styles.count}>
          {filteredWords.length} {filteredWords.length === 1 ? 'word' : 'words'}
        </div>
        <Link to="/content-manager" className="btn btn-primary">
          Add New Word
        </Link>
      </div>
      
      <div className={styles.importExportControls}>
        <button 
          className={styles.exportButton} 
          onClick={exportWords}
          disabled={words.length === 0}
        >
          Export Words
        </button>
        <div className={styles.importWrapper}>
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileSelect}
            accept=".json"
            id="import-words-page"
            className={styles.fileInput}
          />
          <label htmlFor="import-words-page" className={styles.importButton}>
            Import Words
          </label>
        </div>
      </div>

      {words.length === 0 ? (
        <div className="card">
          <h3>No Words Yet</h3>
          <p>Start adding Polish words to your collection!</p>
          <Link to="/content-manager" className="btn btn-primary">
            Add Your First Word
          </Link>
        </div>
      ) : (
        <>
          {filteredWords.length === 0 ? (
            <div className="card">
              <h3>No Matching Words</h3>
              <p>Try a different search term</p>
            </div>
          ) : (
            <div className={styles.wordsTable}>
              <table>
                <thead>
                  <tr>
                    <th 
                      className={sortBy === 'polish' ? styles.sorted : ''}
                      onClick={() => handleSortChange('polish')}
                    >
                      Polish 
                      {sortBy === 'polish' && (
                        <span className={styles.sortIcon}>
                          {sortOrder === 'asc' ? '▲' : '▼'}
                        </span>
                      )}
                    </th>
                    <th 
                      className={sortBy === 'english' ? styles.sorted : ''}
                      onClick={() => handleSortChange('english')}
                    >
                      English
                      {sortBy === 'english' && (
                        <span className={styles.sortIcon}>
                          {sortOrder === 'asc' ? '▲' : '▼'}
                        </span>
                      )}
                    </th>
                    <th>Example</th>
                    <th>Notes</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {sortedWords.map(word => (
                    <tr key={word.id}>
                      <td className={styles.polish}>{word.polish}</td>
                      <td>{word.english}</td>
                      <td className={styles.example}>{word.example}</td>
                      <td className={styles.notes}>{word.notes}</td>
                      <td>
                        <button
                          className={styles.deleteButton}
                          onClick={() => handleDeleteWord(word.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default WordsPage;
